mapper.select:
SELECT *
  FROM (SELECT ROWNUM R_NUM, CUST.*
          FROM (SELECT *
                  FROM CUSTOMER
                 ORDER BY CUST_NO DESC
               ) CUST
        )
 WHERE R_NUM BETWEEN #{begin} AND #{end}

mapper.update:
UPDATE CUSTOMER 
SET CUST_NAME = #{cust_name}
  , CUST_ID = #{cust_id}
  , CUST_PWD = #{cust_pwd}
  , CUST_PHONE = #{cust_phone}
  , CUST_EMAIL= #{cust_email} 
  , CUST_BIRTH = TO_DATE(#{cust_birth}, 'yyyy-mm-dd')
WHERE CUST_NO = #{cust_no}

UPDATE CUSTOMER
SET CUST_EXIST = 0
WHERE CUST_NO = #{cust_no}

mapper.select:
SELECT *
  FROM (SELECT ROWNUM R_NUM, REV.*
          FROM (SELECT *
                  FROM RESERVE
                 ORDER BY REV_NO DESC
               ) REV
        )
 WHERE R_NUM BETWEEN #{begin} AND #{end}

mapper.update:
UPDATE RESERVE
SET REV_NO = #{rev_no}
  , HOTEL_NO = #{hotel_no}
  , CUST_NO = #{cust_no}
  , REV_ROOM_NO = #{rev_room_no}
  , REV_IN= TO_DATE(#{rev_in}, 'yyyy-mm-dd') 
  , REV_OUT = TO_DATE(#{rev_out}, 'yyyy-mm-dd')
  , REV_PRICE = #{rev_price}
  , REV_EXIST = #{rev_exist}
  , REV_NAME = #{rev_name}
  , REV_EMAIL = #{rev_email}
  , REV_PHONE = #{rev_phone}
WHERE REV_NO = #{rev_no}

UPDATE RESERVE
SET REV_EXIST = 0
WHERE REV_NO = #{rev_no}

mapper.select:
SELECT *
  FROM (SELECT ROWNUM R_NUM, HOTEL.*
          FROM (SELECT *
                  FROM HOTEL
                 ORDER BY HOTEL_NO DESC
               ) HOTEL
        )
 WHERE R_NUM BETWEEN #{begin} AND #{end}

SELECT *
  FROM (SELECT ROWNUM R_NUM, ROOM.*
          FROM (SELECT *
                  FROM ROOM
                 ORDER BY ROOM_NO DESC
               ) ROOM
        )
 WHERE R_NUM BETWEEN #{begin} AND #{end}

SELECT *
  FROM (SELECT ROWNUM R_NUM, QNA.*
          FROM (SELECT *
                  FROM QNA
                 ORDER BY QNA_NO DESC
               ) QNA
        )
 WHERE R_NUM BETWEEN #{begin} AND #{end}

SELECT * FROM QNA 
WHERE QNA_NO = #{qna_no}
ORDER BY QNA_NO
